import React, { useState, useEffect } from 'react'
import { AppBar, Avatar, Typography, Toolbar, Button } from '@material-ui/core'
import { Link, useNavigate, useLocation } from 'react-router-dom'
import { useDispatch } from 'react-redux'
import useStyles from './styles'
import Input from '../Auth/Input'

const Navbar = () => {
    const classes = useStyles()
    const navigate = useNavigate()
    const location = useLocation()
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')))
    const dispatch = useDispatch()

    const logout = () => {
      dispatch({ type: 'LOGOUT' })

      navigate.push('/')

      setUser(null)
    }


    useEffect(() => {
      const token = user?.token

      // JWT...

      setUser(JSON.parse(localStorage.getItem('profile')))
    }, [location])

    return (
    <AppBar className={classes.appBar} position="static" color="inherit">
        <div className={classes.brandContainer}>
      <Link to='/' style={{ textDecoration: 'none' }}><Typography className={classes.heading} variant="h2" align="center">Snap Memories</Typography></Link>
        </div>
        <Toolbar className={classes.toolbar}>
          {user ? (
            <div className={classes.profile}>
              <Avatar className={classes.purple} alt={user?.result.name} src={user?.result.imageUrl}>{user?.result.name.charAt(0)}</Avatar>
              <Typography className={classes.userName} variant='h6'>{user.result.name}</Typography>
              <Button variant='contained' size='small' className={classes.logout} color='secondary' onClick={logout}>Logout</Button>
            </div>
          ) : (
            <Button component={Link} to='/auth' size='small' variant='outlined' color='primary'>Sign In</Button>
          )}
        </Toolbar>
    </AppBar>
    )
}

export default Navbar
