import React from 'react'
import { Card, CardActions, CardContent, CardMedia, Button, ButtonBase, Typography } from '@material-ui/core/';
import FavoriteIcon from '@material-ui/icons/Favorite';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import DeleteIcon from '@material-ui/icons/Delete'
//import MoreHorizonIcon from '@material-ui/icons/MoreHoriz'
import EditIcon from '@material-ui/icons/Edit';
import moment from 'moment'
import useStyles from './styles'
import { useDispatch } from 'react-redux'

import { deletePost, likePost } from '../../../actions/posts'
function Post({ post, setCurrentId}) {

  const classes = useStyles();
  const dispatch = useDispatch()
  const user = JSON.parse(localStorage.getItem('profile'))

  const Likes = () => {
    if (post.likes.length > 0) {
      return post.likes.find((like) => like === user?.result?._id)
        ? (
          <><FavoriteIcon fontSize="small" />&nbsp;{post.likes.length > 2 ? `You and ${post.likes.length - 1} others` : `${post.likes.length} like${post.likes.length > 1 ? 's' : ''}` }</>
        ) : (
          <><FavoriteBorderIcon fontSize="small" />&nbsp;{post.likes.length} {post.likes.length === 1 ? 'Like' : 'Likes'}</>
        );
    }

    return <><FavoriteBorderIcon fontSize="small" />&nbsp;Like</>;
  };

  return (
    <Card className={classes.card} raised elevation={6}>
      <ButtonBase className={classes.cardActions} onClick={openPosts}
      >

      <CardMedia className={classes.media} image={post.selectedFile || "https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png"} title={post.title}/>
      <div className={classes.overlay}>
        <Typography variant='h6'>{post.name}</Typography>
        <Typography variant='body2'>{moment(post.createdAt).fromNow()}</Typography>
      </div>
      {(user?.result?._id === post?.creator) && (
        <div className={classes.overlay2}>
          <Button style={{color: 'white'}} size="small" onClick={() =>  setCurrentId(post._id)}>
            <EditIcon fontSize='medium'/>
          </Button>
        </div>
      )}
        <div className={classes.details}>
          <Typography color='textSecondary' variant='body2'>{post.tags.map((tags) => `#${tags} `)}</Typography>
        </div>
        <Typography gutterBottom variant='h5' className={classes.title}>{post.title}</Typography>
        <CardContent>
        <Typography variant='body2' color="textSecondary" component="p">{post.message}</Typography>
        </CardContent>
      </ButtonBase>
        <CardActions className={classes.cardActions}>
          <Button size="small" disabled={!user?.result} onClick={() => dispatch(likePost(post._id))}>
            <Likes />
          </Button>
          {(user?.result?._id === post?.creator) && (
          <Button size="small" color='primary' onClick={() => dispatch(deletePost(post._id))}>
            <DeleteIcon fontSize='small' />
             Delete
            </Button>
          )}
        </CardActions>
    </Card>
  )
}

export default Post
