import React, { useEffect, useState } from 'react'
import { Container, AppBar, Typography, Grow, Grid, Stack, Box } from '@mui/material'
import { getPosts } from './actions/posts';
import Posts from './components/Posts/Posts';
import Form from './components/Form/Form';
import { useDispatch } from 'react-redux';
import DarkMode from './darkMode';
import Button from "@material-ui/core/Button";
import memories from './Images/memories.png'
import './index.css'
import useStyles from './styles'
const App = () => {

    const [currentId, setCurrentId] = useState(null)
    const classes = useStyles()
    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(getPosts());
      }, [currentId, dispatch]);


    return (
        <Container maxWidth="lg">
        <AppBar className={classes.appBar} position="static" color="inherit">
          <Typography variant="h2" align="center" fontSize={45}>Snap Memories</Typography>
          <Box m={3}>
            <Stack direction="row" justifyContent="space-between">
            <Typography className={classes.heading} variant="h2" align="center" fontSize={45}>Snap Memories</Typography>
            <DarkMode></DarkMode>
          </Stack>
          </Box>
        </AppBar>
        <Grow in>
          <Container>
            <Grid container justify="space-between" alignItems="stretch" spacing={3}>
              <Grid item xs={12} sm={7}>
                <Posts setCurrentId={setCurrentId}/>
              </Grid>
              <Grid item xs={12} sm={4}>
                <Form currentId={currentId} setCurrentId={setCurrentId}/>
              </Grid>
            </Grid>
          </Container>
        </Grow>
      </Container>
    )
}

export default App
